mapper.select:
SELECT user_type_name
FROM user
WHERE id=#{0}

SELECT score_reduce
FROM credit_rule
WHERE user_type_name= #{0}
AND credit_type=1;

SELECT rule_name
FROM credit_rule
WHERE user_type_name = #{0}
AND credit_type=1;

mapper.insert:
INSERT INTO credit_record (user_id, booking_record_id, score_reduce, credit_rule_name)
VALUE
(#{0}, #{1}, #{2}, #{3});

mapper.update:
UPDATE user
SET credit_score = credit_score - #{1}
WHERE id = #{0}

UPDATE user
SET ban_duration = 1
WHERE id = #{0}

